		topology.set_controller_id();
		topology.setControllerIP(ControllerIP1, ControllerIP2, ControllerIP3);
		topology.setNextControllerID(topology.globalNode);
		topology.showTest();
		
		//フローエントリ設定時間の計測開始
		long start = System.currentTimeMillis();
		
