		BorderNodeFlow bordernodeflow = new BorderNodeFlow(topology.tiesetList, topology.globalNode, topology.globalGraph, topology.TiesetGraph);
		bordernodeflow.makeBorderFlow();
		
		EdgeNodeFlow edgenodeflow = new EdgeNodeFlow(topology.tiesetList, topology.globalNode, topology.globalGraph, topology.TiesetGraph);
		edgenodeflow.makeEdgeFlow();
		
